package combined.daveagp;
import stdlibpack.*;
public class Kettles {
   public static void sing(int n) {
      // print three lines
      System.out.println(n + " kettles of tea on the wall");
      System.out.println(n + " kettles of tea");
      System.out.println("take one down, pass it around");      
      if (n > 1) {
         // sing fourth line, with exclamation point!
         System.out.println(n-1 + " kettles of tea on the wall!");
         // call sing recursively on remaining kettles
         sing(   n-1 );
      }
      else { 
         // sing the final line, with exclamation point!
         System.out.println("no more kettles of tea on the wall!");
      }
   }

   public static void main(String[] args) {
      sing(Integer.parseInt(args[0]));
   }
}

